    (function(a) { 
        var str = "http://www.studylight.org/isb/view.cgi?number="
        if(a.indexOf(str) === 0) {
            a = a.substr(str.length);
            if(a.indexOf("0") === 0) {
                return "strong:/H" + a;
            } else {
                return "strong:/G" + a;
            }

        } else {
            return a;
        }
    })
