site.pharseIn:
(function(a) { 
    if(a[0] == 'G' || a[0] == 'g') {
        a = a.substr(1);
        return "http://www.studylight.org/isb/view.cgi?number="+a+"&tool=grk";
    } else if(a[0] == 'H' || a[0] == 'h') {
        a = a.substr(1);
        return "http://www.studylight.org/isb/view.cgi?number=0"+a;
    }
    return "http://www.studylight.org/isb/";
 })

site.pharseOut:

(function(a) { 
    var str = "http://www.studylight.org/isb/view.cgi?number="
    if(a.indexOf(str) === 0) {
        a = a.substr(str.length);
        if(a.indexOf("0") === 0) {
            return "strong:/H" + a;
        } else {
            return "strong:/G" + a;
        }

    } else {
        return a;
    }
})
